import React, {useState} from "react";

function CreateArea(){
    const [note, setNote] = useState({
        title: "",
        content: ""
    });
    function handleChange(event){
      var  {name, value} = event.target;

      setNote(prevValue => {
        return{
        ...prevValue,
        [name]:value
        }
      });
      console.log(note.title);
      console.log(note.content);
    }
    return(
        <div>
        <form>
        <input
        onChange ={handleChange}
        name="title"
        value={note.title}
         placeholder="Title..."/>

    
        <textarea
        onChange ={handleChange}
        name="content"
        value={note.content}
        placeholder = "Content..."
        rows="3"
       />

       <button>Add</button>
       </form>
        </div>
    );

}

export default CreateArea;
